    const [fname, setfname]=useState("");
    const [comment, setcomment]=useState("");
  
    const handleSubmit = (event) => {
      console.log(`
        Email: ${email}
        Password: ${password}
        Country: ${country}
        Accepted Terms: ${acceptedTerms}
      `);
      
      event.preventDefault();
    }










  return (props.trigger)?(
    <div className='popup'>
        <div className="popup-inner">
