import React, { useEffect, useState } from 'react';
import styles from  './../Popup/style.css'

function Popup(props) {


    const [email, setEmail] = React.useState("");
    const [password, setPassword] = React.useState("");
    const [country, setCountry] = React.useState("");
    const [acceptedTerms, setAcceptedTerms] = React.useState(false);


    const [fname, setfname]=useState("");
    const [comment, setcomment]=useState("");
    const [rating, setrating]=useState("");

    const initialvalues={fname:"", comment:""}
    const [formvalues, setformvalues]=useState(initialvalues);
    const [formerrors, setformerrors]=useState({});
    const [issubmit,setissubmit] =useState(false);
  
   



   
    const handlchange=(e)=>{
      console.log(e.target);
      const {name, value}=e.target;
      setformvalues({...fromvalues,[name]:value});
      console.log(fromvalues);

    }

    const handleSubmit = (event) => {
      event.preventDefault();
      console.log(`
        fname: ${fname}
        comment: ${comment}
        rating: ${rating}
        Accepted Terms: ${acceptedTerms}
      `);

      setformerrors(validate(fromvalues));
      setissubmit(true);
     
    }

    useEffect(()=>{
      console.log(fromvalues);
      if(Object.keys(fromvalues).length ===0 && issubmit  ){
        console.log(fromvalues);
      }
    },[formerrors]);



    const validate=(values)=>{
      const errors={};
      
      if(!values.fname){
        errors.fname="Name Required!";
      }

      if(!values.comment){
        errors.comment="Comment Required!";
      }


      return errors;

    }




  return (props.trigger)?(
    <div className='popup'>
        <div className="popup-inner">
            <button className="close-btn">Close</button>
            {props.children}
        </div>

        <form onSubmit={handleSubmit}    className={styles.formcon}>
          <pre>{JSON.stringify(fromvalues)}</pre>
      <h1>Submit Comment</h1>


      <label>
        RATING
        <input
          name="text"
          type="number"
          value={fromvalues.rating}
          onChange={e => setrating(e.target.value)}
          required />
      </label>

      <label>
        Name
        <input
          name="fname"
          type="text"
          value={fromvalues.fname}
          onChange={handlchange}
          required />
          
      </label>
      <p>{formerrors.fname}</p>
      
      <label>
       Comment
        <input
          name="comment"
          type="text"
          value={formvalues.comment}
          onChange={handlchange}
          required />
      </label>

     

      <label>
        <input
          name="acceptedTerms"
          type="checkbox"
          onChange={handlchange}
          required />
        I accept the terms of service        
      </label>

      <button>Submit</button>
    </form>
      
    </div>
  ): "";
}

export default Popup;
